package com.example.mvvm;import androidx.lifecycle.LiveData;import androidx.lifecycle.MutableLiveData;import androidx.lifecycle.ViewModel;public class CalcularViewModel extends ViewModel {    public MutableLiveData<Integer> resultado;    public CalcularViewModel() {        resultado = new MutableLiveData<>();    }    public LiveData<Integer>  getResultado(){            return resultado;    }    public  void calcular(int numero){        resultado.setValue(calcularUseCase.calcular(numero));    }}